int CreateJson::base64_encode(const unsigned char *in,  unsigned long len, 
                        unsigned char *out)
{
   unsigned long i, len2, leven;
   unsigned char *p;
   /* valid output size ? */
   len2 = 4 * ((len + 2) / 3);
   p = out;
   leven = 3*(len / 3);
   for (i = 0; i < leven; i += 3) {
       *p++ = codes[in[0] >> 2];
       *p++ = codes[((in[0] & 3) << 4) + (in[1] >> 4)];
       *p++ = codes[((in[1] & 0xf) << 2) + (in[2] >> 6)];
       *p++ = codes[in[2] & 0x3f];
       in += 3;
   }
   /* Pad it if necessary...  */
   if (i < len) {
       unsigned a = in[0];
       unsigned b = (i+1 < len) ? in[1] : 0;
       unsigned c = 0;

       *p++ = codes[a >> 2];
       *p++ = codes[((a & 3) << 4) + (b >> 4)];
       *p++ = (i+1 < len) ? codes[((b & 0xf) << 2) + (c >> 6)] : '=';
       *p++ = '=';
   }

   /* append a NULL byte */
   *p = '\0';

   return p - out;
}

int CreateJson::base64_decode(const unsigned char *in, unsigned char *out)
{
	unsigned long t, x, y, z;
	unsigned char c;
	int	g = 3;

	for (x = y = z = t = 0; in[x]!=0;) {
		c = mapp[in[x++]];
		if (c == 255) return -1;
		if (c == 253) continue;
		if (c == 254) { c = 0; g--; }
		t = (t<<6)|c;
		if (++y == 4) {
//			if (z + g > *outlen) { return CRYPT_BUFFER_OVERFLOW; }
			out[z++] = (unsigned char)((t>>16)&255);
			if (g > 1) out[z++] = (unsigned char)((t>>8)&255);
			if (g > 2) out[z++] = (unsigned char)(t&255);
			y = t = 0;
		}
	}
//	if (y != 0) {
//		return -1;
//	}
	return z;
}


char *CreateJson::_getmac(char *mac,int len){

	struct   ifreq   ifreq; 
	int   sock; 


	if((sock=socket(AF_INET,SOCK_STREAM,0)) <0) 
	{ 
		perror( "socket "); 
	} else{
		strcpy(ifreq.ifr_name,"eth0"); 
		if(ioctl(sock,SIOCGIFHWADDR,&ifreq) <0) 
		{ 
	 		perror( "get mac error!\n"); 
		} 
	}

	snprintf(mac,len, "%02x:%02x:%02x:%02x:%02x:%02x", 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[0], 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[1], 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[2], 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[3], 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[4], 
            (unsigned   char)ifreq.ifr_hwaddr.sa_data[5]); 
		//printf("mac:%s\n",mac);
	return mac;
}


int CreateJson::initcache(){
	if(NULL == p||NULL == d||m == NULL){
		p = (unsigned char *)malloc(BUFFERLEN);
		d = (unsigned char *)malloc(BUFFERLEN*2);
		m = (char *)malloc(BUFFERLEN*2 + 1024);
		l = BUFFERLEN;
	}
}

char *CreateJson::createheart(){
	
	char mac[128] = {'\0'};
	char ht[512];

	initcache();

	_getmac(mac,128);
	snprintf(m,BUFFERLEN*2,fmt_heart,1,mac,0);
	return m;
}
